! function (a) {
    "function" == typeof define && define.amd ? define(["jquery", "jquery.validate.min"], a) : a(jQuery)
}(function (a) {
    a.extend(a.validator.messages, {
        required: "必填",
        remote: "请修正此栏位",
        email: "请输入有效的电子邮件",
        url: "请输入有效的网址",
        date: "请输入有效的日期",
        dateISO: "请输入有效的日期 (YYYY-MM-DD)",
        number: "请输入正确的数字",
        digits: "只能输入数字",
        creditcard: "请输入有效的信用卡号码",
        equalTo: "你的输入不相同",
        extension: "请输入有效的后缀",
        maxlength: a.validator.format("最多 {0} 个字"),
        minlength: a.validator.format("最少 {0} 个字"),
        rangelength: a.validator.format("请输入长度为 {0} 至 {1} 之间的字串"),
        range: a.validator.format("请输入 {0} 至 {1} 之间的数值"),
        max: a.validator.format("请输入不大于 {0} 的数值"),
        min: a.validator.format("请输入不小于 {0} 的数值")
    });
    //用户名称校验规则
    a.validator.addMethod("userName",function(v,element,params){
 	   if(v.length < 6){
 	    return false;
 	   }
 	   var reg = /^[a-zA-Z0-9\u4e00-\u9fa5]+$/;
 	   return reg.test(v);
 	},"必须是由大于6位的字母、数字、下划线组成！");
    //电话校验规则
 	a.validator.addMethod("phone",function(v,element,params){
 		var regExp = /^(([0\+]\d{2,3}-)?(0\d{2,3})-)(\d{7,8})(-(\d{3,}))?$/;
 		return regExp.test(v);
 	},"电话号码不合法，请重新输入！");
 	//手机校验规则
 	a.validator.addMethod("mobile",function(v,element,params){
 		var regExp = /^0{0,1}1[3|4|5|6|7|8|9][0-9]{9}$/;
 		return regExp.test(v);
 	},"手机号不合法，请重新输入！");
 	//邮编校验规则
 	a.validator.addMethod("zipCode",function(v,element,params){
 		var regExp = /^[0-9]{6}$/;
 		return regExp.test(v);
 	},"邮编号码不合法，请重新输入！");
 	
 	a.validator.setDefaults({
// 		debug: true,
 		ignore : ":disabled,:hidden",
 		errorPlacement: function(error, element) { 
 		     error.appendTo(element.parent()); 
 		}
	});
});